$(document).ready(function(){var checkbox_toggles={'id_have_past_illness':'div_id_reason_past_illness','id_have_physical_limits':'div_id_reason_physical_limits','id_have_allergies':'div_id_allergies','id_is_pregnant':'div_id_weeks_pregnant','id_have_other_condition':'div_id_other_medical_condition','id_have_gastric_problem':'div_id_gastric_problem','id_have_mental_illness':'div_id_mental_illness','id_have_kidney_problem':'div_id_kidney_problem','id_take_regular_medication':'div_id_regular_medication','id_have_bone_problem':'div_id_bone_problem'};function bind_checkbox(id){$('#'+id).click(function(){toggle($(this).attr('id'));});}
function toggle(id){var $checkbox=$('#'+id);var toggle_id=checkbox_toggles[id];if($checkbox.is(':checked')){$('#'+toggle_id).show();$('#'+toggle_id).find('label').addClass('required');}else{$('#'+toggle_id).hide();$('#'+toggle_id).find('label').removeClass('required');}}
for(var checkbox in checkbox_toggles){toggle(checkbox);bind_checkbox(checkbox);}});;